include(PATH_THIRD.'gmaps/config.php');
 
class Gmaps_upd_430
{
	private $EE;
	private $version = '4.3.0';
	
	// ----------------------------------------------------------------

	/**
	 * Construct method
	 *
	 * @return      boolean         TRUE
	 */
	public function __construct()
	{		
		//load the classes
		ee()->load->dbforge();

		//require the settings
		require PATH_THIRD.'gmaps/settings.php';
	}

	// ----------------------------------------------------------------
	
	/**
	 * Run the update
	 *
	 * @return      boolean         TRUE
	 */
	public function run_update()
	{
		ee()->db->where('class', 'Gmaps');
		ee()->db->where('method', 'gmaps_api');
		ee()->db->delete('actions');

		//clear cache
		ee()->db->truncate('gmaps_cache');

	}
}